import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const handleAddTask = () => {
    if (task.trim()) {
      const updatedTasks = [...tasks, task];
      setTasks(updatedTasks);
      localStorage.setItem('tasks', JSON.stringify(updatedTasks)); // Save tasks to localStorage
      setTask('');
      setShowModal(true);
      setTimeout(() => setShowModal(false), 2000); // Hide modal after 2 seconds
    }
  };

  const handleViewTasks = () => {
    // Open task list in a new tab
    const newWindow = window.open();
    newWindow.document.write(`
      <html>
        <head><title>Your Tasks</title></head>
        <body>
          <h3>Your Tasks</h3>
          <ul>
            ${tasks.length > 0 ? tasks.map(task => `<li>${task}</li>`).join('') : '<p>No tasks available.</p>'}
          </ul>
        </body>
      </html>
    `);
  };

  return (
    <div className="container">
      <header>
        <h1>Hello Folk</h1>
        <p>"Hello Hi"</p>
      </header>
      <div className="task-input">
        <h2>Add Your Task</h2>
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Enter your task"
        />
      </div>
      <div className="buttons">
        <button className="add-btn" onClick={handleAddTask}>Add</button>
        <button className="view-btn" onClick={handleViewTasks}>View Your Tasks</button>
      </div>

      {/* Modal Pop-up */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Task Successfully Added!</h3>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
