import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);
  const [completedTasks, setCompletedTasks] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  const handleAddTask = () => {
    if (task.trim()) {
      const updatedTasks = [...tasks, task];
      setTasks(updatedTasks);
      localStorage.setItem('tasks', JSON.stringify(updatedTasks)); // Save tasks to localStorage
      setTask('');
      setShowModal(true);
      setModalMessage('Task Added Successfully!');
      setTimeout(() => setShowModal(false), 2000); // Hide modal after 2 seconds
    }
  };

  const handleTaskClick = (taskToMove) => {
    const updatedTasks = tasks.filter((t) => t !== taskToMove);
    const updatedCompletedTasks = [...completedTasks, taskToMove];
    setTasks(updatedTasks);
    setCompletedTasks(updatedCompletedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    localStorage.setItem('completedTasks', JSON.stringify(updatedCompletedTasks));

    setShowModal(true);
    setModalMessage('Task Moved to Completed!');
    setTimeout(() => setShowModal(false), 2000); // Hide modal after 2 seconds
  };

  const handleShowCompletedTasks = () => {
    const newWindow = window.open();
    newWindow.document.write(`
      <html>
        <head><title>Completed Tasks</title></head>
        <body>
          <h3>Completed Tasks</h3>
          <ul>
            ${completedTasks.length > 0
              ? completedTasks.map((task) => `<li>${task}</li>`).join('')
              : '<p>No completed tasks.</p>'}
          </ul>
        </body>
      </html>
    `);
  };

  const handleViewYourTasks = () => {
    const newWindow = window.open();
    newWindow.document.write(`
      <html>
        <head><title>Your Tasks</title></head>
        <body>
          <h3>Your Tasks</h3>
          <ul>
            ${tasks.length > 0
              ? tasks.map((task) => `<li>${task}</li>`).join('')
              : '<p>No tasks available.</p>'}
          </ul>
        </body>
      </html>
    `);
  };

  return (
    <div className="container">
      <header>
        <h1>Hello Folk</h1>
        <p>"Hello Hi"</p>
      </header>
      <div className="task-input">
        <h2>Add Your Task</h2>
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Enter your task"
        />
      </div>
      <div className="buttons">
        <button className="add-btn" onClick={handleAddTask}>Add</button>
        <button className="view-btn" onClick={handleViewYourTasks}>View Your Tasks</button>
        <button className="show-completed-btn" onClick={handleShowCompletedTasks}>Show Completed Tasks</button>
      </div>

      {/* Modal Pop-up for Task Actions */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>{modalMessage}</h3>
          </div>
        </div>
      )}

      {/* Task List */}
      <div className="task-list">
        <h3>Your Tasks</h3>
        <ul>
          {tasks.length > 0 ? (
            tasks.map((task, index) => (
              <li key={index} onClick={() => handleTaskClick(task)}>{task}</li>
            ))
          ) : (
            <p>No tasks available.</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default App;
