import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);
  const [showTasks, setShowTasks] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const handleAddTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask('');
      setShowModal(true); // Show the modal when a task is added
      setTimeout(() => setShowModal(false), 2000); // Hide the modal after 2 seconds
    }
  };

  const handleViewTasks = () => {
    setShowTasks(!showTasks);
  };

  return (
    <div className={`container ${showModal ? 'blur' : ''}`}>
      <header>
        <h1>Hello Folk</h1>
        <p>"Hello Hi"</p>
      </header>
      <div className="task-input">
        <h2>Add Your Task</h2>
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Enter your task"
        />
      </div>
      <div className="buttons">
        <button className="add-btn" onClick={handleAddTask}>Add</button>
        <button className="view-btn" onClick={handleViewTasks}>View Your Tasks</button>
      </div>

      {/* Modal Pop-up */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Task Successfully Added!</h3>
          </div>
        </div>
      )}

      {showTasks && (
        <div className="task-list">
          <h3>Your Tasks:</h3>
          <ul>
            {tasks.map((task, index) => (
              <li key={index}>{task}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
