import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import './App.css';

const App = () => {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const handleAddTask = () => {
    if (task.trim()) {
      const updatedTasks = [...tasks, task];
      setTasks(updatedTasks);
      localStorage.setItem('tasks', JSON.stringify(updatedTasks)); // Save tasks to localStorage
      setTask('');
      setShowModal(true);
      setTimeout(() => setShowModal(false), 2000); // Hide modal after 2 seconds
    }
  };

  return (
    <Router>
      <div className="container">
        <header>
          <h1>Hello Folk</h1>
          <p>"Hello Hi"</p>
        </header>
        <div className="task-input">
          <h2>Add Your Task</h2>
          <input
            type="text"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            placeholder="Enter your task"
          />
        </div>
        <div className="buttons">
          <button className="add-btn" onClick={handleAddTask}>Add</button>
          <Link to="/task-list">
            <button className="view-btn">View Your Tasks</button>
          </Link>
        </div>

        {/* Modal Pop-up */}
        {showModal && (
          <div className="modal">
            <div className="modal-content">
              <h3>Task Successfully Added!</h3>
            </div>
          </div>
        )}
      </div>

      {/* Routes */}
      <Routes>
        <Route
          path="/task-list"
          element={
            <div className="task-page">
              <h3>Your Tasks</h3>
              <ul>
                {tasks.length > 0 ? (
                  tasks.map((task, index) => (
                    <li key={index}>{task}</li>
                  ))
                ) : (
                  <p>No tasks available.</p>
                )}
              </ul>
            </div>
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
